import React, { useState, useEffect } from "react";
import * as Yup from "yup";
import styled from "styled-components";

const Month = styled.div`
  display: flex;
  margin: 2%;
  width: 100%;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const Calc = styled.form`
  display: flex;
  width: 25%;
  flex-direction: column;
  margin: 3%;
  padding: 2%;
`;

const Label = styled.label`
  display: flex;
  margin: 2%;
  width: 100%;
  align-items: center;
  text-align: center;
  justify-content: center;
`;

const MonthlyCalc = (props, { errors, touched, values, status }) => {
  const [monthlyBudget, setMonthlyBudget] = useState({ monthlyIncome: 0 });

  const submitHandler = event => {
    event.preventDefault();
    setMonthlyBudget();
    console.log("in submitHandler");
  };

  const changeHandler = event => {
    const parse = parseInt(event.target.value);
    setMonthlyBudget(prevValue => {
      [...prevValue];
    });
    console.log("in change handler", parse);
  };

  return (
    <div className="monthlyForm">
      <h1>Monthly Expenditures</h1>

      <Calc>
        <Month>
          <label>Monthly Income</label>
          <input
            type="text"
            name="monthlyIncome"
            onChange={changeHandler}
            value={monthlyBudget.monthlyIncome}
          ></input>
        </Month>
        <Label>Transportation Expense?</Label>
        <input
          type="text"
          name="transportation"
          placeholder="Transportation"
          onChange={changeHandler}
        ></input>
        <Label>Food Expense?</Label>
        <input
          type="text"
          name="food"
          placeholder="Cost of Food"
          onChange={changeHandler}
        ></input>
        <Label>Health Insurance?</Label>
        <input
          type="text"
          name="healthInsurance"
          placeholder="Health Insurance Cost"
          onChange={changeHandler}
        ></input>
        <Label>Car Insurance?</Label>
        <input
          type="text"
          name="carInsurance"
          placeholder="Car Insurance Cost"
          onChange={changeHandler}
        ></input>
        <Label>Car Note Cost?</Label>
        <input type="text" name="carNote" placeholder="Car Note Cost"></input>
        <Label>Personal Loans?</Label>
        <input
          type="text"
          name="personalLoans"
          placeholder="Personal Loans"
          onChange={changeHandler}
        ></input>
        <Label>Other Costs</Label>
        <input
          type="text"
          name="miscMonthlyExpense"
          placeholder="Other"
          onChange={changeHandler}
        ></input>
      </Calc>
      <button type="submit" onClick={submitHandler}>
        Calculate
      </button>
    </div>
  );
};
export default MonthlyCalc;
